import { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { CircularProgress, Button, Typography, Container, Box } from "@mui/material";

// Определяем API URL (используем .env или fallback на http://app:5000)
const API_URL =
  process.env.NODE_ENV === "development"
    ? "http://localhost:5000"
    : process.env.REACT_APP_API_URL || "http://app:5000";

const UploadForm = () => {
    const [files, setFiles] = useState([]);
    const [finalResult, setFinalResult] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        setFiles([...event.target.files]);
    };

    const handleUpload = async () => {
        if (files.length === 0) {
            toast.error("Выберите хотя бы один файл!");
            return;
        }

        const formData = new FormData();
        formData.append("file", files[0]); // Загружаем только один файл

        setLoading(true);

        try {
            const response = await axios.post(`${API_URL}/predict/`, formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });

            const data = response.data;

            if (!data.final_class || !data.results) {
                toast.error("Ошибка в полученных данных!");
                return;
            }

            // Берём confidence только у той модели, которая определила финальный класс
            const finalClass = data.final_class;
            const confidence = data.results[Object.keys(data.results).find(model => data.results[model].class === finalClass)]?.confidence;

            // Переводим в проценты
            const confidencePercentage = confidence ? (confidence * 100).toFixed(2) : "N/A";

            setFinalResult(`${confidencePercentage}%`);
            toast.success("Файл успешно загружен!");
        } catch (error) {
            console.error("Upload error:", error.response ? error.response.data : error);

            if (error.response) {
                toast.error(`Ошибка ${error.response.status}: ${error.response.data?.message || "Неизвестная ошибка"}`);
            } else if (error.request) {
                toast.error("Ошибка подключения к серверу. Проверьте API URL.");
            } else {
                toast.error("Ошибка отправки запроса!");
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container maxWidth="sm">
            <Box textAlign="center" mt={4} p={3} boxShadow={3} borderRadius={2}>
                <Typography variant="h5" gutterBottom>
                    Загрузка изображений
                </Typography>

                <input type="file" onChange={handleFileChange} />

                <Box mt={2}>
                    <Button variant="contained" color="primary" onClick={handleUpload} disabled={loading}>
                        {loading ? <CircularProgress size={24} color="inherit" /> : "Загрузить"}
                    </Button>
                </Box>

                {finalResult && (
                    <Box p={3} bgcolor="#e3f2fd" borderRadius={2} mt={3}>
                        <Typography variant="h6">📊 Итоговый результат:</Typography>
                        <Typography variant="h4" fontWeight="bold" color="primary">
                            {finalResult}
                        </Typography>
                    </Box>
                )}
            </Box>
        </Container>
    );
};

export default UploadForm;
