import { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { CircularProgress, Button, Typography, Container, Box } from "@mui/material";

// Определяем API URL (используем .env или fallback на http://app:5000)
const API_URL =
  process.env.NODE_ENV === "development"
    ? "http://localhost:5000"
    : process.env.REACT_APP_API_URL || "http://app:5000";

const UploadForm = () => {
    const [files, setFiles] = useState([]);
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        setFiles([...event.target.files]);
    };

    const handleUpload = async () => {
        if (files.length === 0) {
            toast.error("Выберите хотя бы один файл!");
            return;
        }

        const formData = new FormData();
        formData.append("file", files[0]); // Пока загружаем только один файл

        setLoading(true);

        try {
            const response = await axios.post(`${API_URL}/predict/`, formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });

            setResult(response.data);
            toast.success("Файл успешно загружен!");
        } catch (error) {
            console.error("Upload error:", error);

            if (error.response) {
                // Сервер ответил, но с ошибкой
                toast.error(`Ошибка ${error.response.status}: ${error.response.data?.message || "Неизвестная ошибка"}`);
            } else if (error.request) {
                // Запрос был сделан, но сервер не ответил
                toast.error("Ошибка подключения к серверу. Проверьте API URL.");
            } else {
                // Что-то пошло не так при настройке запроса
                toast.error("Ошибка отправки запроса!");
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container maxWidth="sm">
            <Box textAlign="center" mt={4} p={3} boxShadow={3} borderRadius={2}>
                <Typography variant="h5" gutterBottom>
                    Загрузка изображений
                </Typography>

                <input type="file" onChange={handleFileChange} />

                <Box mt={2}>
                    <Button variant="contained" color="primary" onClick={handleUpload} disabled={loading}>
                        {loading ? <CircularProgress size={24} color="inherit" /> : "Загрузить"}
                    </Button>
                </Box>

                {result && (
                    <Box mt={3}>
                        <Typography variant="h6">Результат:</Typography>
                        <pre>{JSON.stringify(result, null, 2)}</pre>
                    </Box>
                )}
            </Box>
        </Container>
    );
};

export default UploadForm;
