import { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { CircularProgress, Button, Typography, Container, Box } from "@mui/material";

const UploadForm = () => {
    const [files, setFiles] = useState([]);
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        setFiles([...event.target.files]);
    };

    const handleUpload = async () => {
        if (files.length === 0) {
            toast.error("Выберите хотя бы один файл!");
            return;
        }

        const formData = new FormData();
        files.forEach(file => formData.append("file", file));

        setLoading(true);

        try {
            const response = await axios.post("http://localhost:8080/api/upload", formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });

            setResult(response.data);
            toast.success("Файл успешно загружен!");
        } catch (error) {
            toast.error("Ошибка загрузки файла!");
            console.error("Upload error:", error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container maxWidth="sm">
            <Box textAlign="center" mt={4} p={3} boxShadow={3} borderRadius={2}>
                <Typography variant="h5" gutterBottom>
                    Загрузка изображений
                </Typography>

                <input
                    type="file"
                    multiple
                    onChange={handleFileChange}
                    style={{ marginBottom: "10px" }}
                />

                <Box mt={2}>
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleUpload}
                        disabled={loading}
                    >
                        {loading ? <CircularProgress size={24} color="inherit" /> : "Загрузить"}
                    </Button>
                </Box>

                {result && (
                    <Box mt={3}>
                        <Typography variant="h6">Результат:</Typography>
                        <pre>{JSON.stringify(result, null, 2)}</pre>
                    </Box>
                )}
            </Box>
        </Container>
    );
};

export default UploadForm;
