import { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { CircularProgress, Button, Typography, Container, Box } from "@mui/material";

// –û–ø—Ä–µ–¥–µ–ª—è–µ–º API URL (–∏—Å–ø–æ–ª—å–∑—É–µ–º .env –∏–ª–∏ fallback –Ω–∞ http://app:5000)
const API_URL =
  process.env.NODE_ENV === "development"
    ? "http://localhost:5000"
    : process.env.REACT_APP_API_URL || "http://app:5000";

const UploadForm = () => {
    const [files, setFiles] = useState([]);
    const [finalClass, setFinalClass] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (event) => {
        setFiles([...event.target.files]);
    };

    const handleUpload = async () => {
        if (files.length === 0) {
            toast.error("–í—ã–±–µ—Ä–∏—Ç–µ —Ö–æ—Ç—è –±—ã –æ–¥–∏–Ω —Ñ–∞–π–ª!");
            return;
        }

        const formData = new FormData();
        formData.append("file", files[0]); // –ó–∞–≥—Ä—É–∂–∞–µ–º —Ç–æ–ª—å–∫–æ –æ–¥–∏–Ω —Ñ–∞–π–ª

        setLoading(true);

        try {
            const response = await axios.post(`${API_URL}/predict/`, formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });

            setFinalClass(response.data.final_class);
            toast.success("–§–∞–π–ª —É—Å–ø–µ—à–Ω–æ –∑–∞–≥—Ä—É–∂–µ–Ω!");
        } catch (error) {
            console.error("Upload error:", error);

            if (error.response) {
                toast.error(`–û—à–∏–±–∫–∞ ${error.response.status}: ${error.response.data?.message || "–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞"}`);
            } else if (error.request) {
                toast.error("–û—à–∏–±–∫–∞ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏—è –∫ —Å–µ—Ä–≤–µ—Ä—É. –ü—Ä–æ–≤–µ—Ä—å—Ç–µ API URL.");
            } else {
                toast.error("–û—à–∏–±–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏ –∑–∞–ø—Ä–æ—Å–∞!");
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container maxWidth="sm">
            <Box textAlign="center" mt={4} p={3} boxShadow={3} borderRadius={2}>
                <Typography variant="h5" gutterBottom>
                    –ó–∞–≥—Ä—É–∑–∫–∞ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π
                </Typography>

                <input type="file" onChange={handleFileChange} />

                <Box mt={2}>
                    <Button variant="contained" color="primary" onClick={handleUpload} disabled={loading}>
                        {loading ? <CircularProgress size={24} color="inherit" /> : "–ó–∞–≥—Ä—É–∑–∏—Ç—å"}
                    </Button>
                </Box>

                {finalClass && (
                    <Box p={3} bgcolor="#e3f2fd" borderRadius={2} mt={3}>
                        <Typography variant="h6">üèÜ –ò—Ç–æ–≥–æ–≤—ã–π –∫–ª–∞—Å—Å:</Typography>
                        <Typography variant="h4" fontWeight="bold" color="primary">
                            {finalClass}
                        </Typography>
                    </Box>
                )}
            </Box>
        </Container>
    );
};

export default UploadForm;
